
/*Actualizando aleatoriamente el STATUS de los registros: BANNED, PENDING, ACTIVE*/
UPDATE users SET STATUS = 'BANNED' WHERE id % 2 = 0;
UPDATE users SET STATUS = 'PENDING' WHERE (id > 28 AND id < 40);
UPDATE users SET STATUS = 'ACTIVE' WHERE STATUS IS NULL;

DELETE FROM users WHERE id > 100;

/*Le ponemos una condición verdadera (WHERE 1 = 1) para que haga la actualización. Sino, nos muestra una alerta para tener cuidado que se hará una actualización de registros SIN condición y que confirmemos.*/
UPDATE users SET image_url = CASE FLOOR(1 + RAND() * 2) WHEN 1 THEN CONCAT('https://randomuser.me/api/portraits/men/', FLOOR(RAND() * 100), '.jpg') WHEN 2 THEN CONCAT('https://randomuser.me/api/portraits/women/', FLOOR(RAND() * 100), '.jpg') END WHERE 1 = 1;